import React from 'react';
import './App.css';

const numbers = [];
const min = 1;
const max = 30;
const randomNumbers = [];

for (let i=1; i<31; i++) {
  numbers.push(i)
}

for(let i = 0; i<12; i++){
  randomNumbers[i]=Math.floor(min + Math.random() * (max - min));

 }


console.log(randomNumbers);
class App extends React.Component {
  constructor() {
    super();

    this.state= {
      ticket: [],
      tickets: [],
      ticketFinished: false,
      numberTicket: 0,
      addClass: false,
      number: null, 
      random: [],
      isPlaying: false,
      test: []
    }

    this.makeTicket=this.makeTicket.bind(this);
    this.addTicket=this.addTicket.bind(this);
    this.colorButton=this.colorButton.bind(this);
  }

  makeTicket(e) {
    e.preventDefault();
    const validateTicket = this.state.numberTicket;
    const ticketInProgres = this.state.ticket;
    if(validateTicket<5){
      if(ticketInProgres.length < 5) {
      const number = e.target.value;
        this.setState({
        addClass: true,
        number: number,
        ticket: [...this.state.ticket, number]
      })
      } else {

      }
    }
  }

  addTicket(e) {
    e.preventDefault();
    const validation = this.state.ticketFinished;
    const ticket =this.state.ticket;
    const numberOfTickets = this.state.numberTicket;
    if(numberOfTickets < 5){
      if (validation || ticket.length>0) {
        this.setState({
              ...this.state,
              tickets: [...this.state.tickets, ticket ],
              ticket: [],
              ticketFinished: false,
              numberTicket: this.state.numberTicket +1
          })
      }
    }else{
        const addButton = document.getElementById('add');
        const playButton = document.getElementById('play');
        addButton.style.display='none';
        playButton.style.display='block'; 
    }
  }

  colorButton(){
    if(this.state.numberTicket==5){
      return true;
    }
  }  

  render() {
    let id= this.state.number;
    let element = document.getElementById(id);
    if(id) {
      element.style.backgroundColor='red'
    }

    const listItems=numbers.map((number, index) => 
       <button key={index} value={number} className="number" id={number} onClick={this.makeTicket}>{number}</button>
        );
    const tickets=this.state.tickets;
    const listTickets= tickets.map((ticket, index) => <p key={index}>{ticket}</p>);

    console.log(this.state.numberTicket);
    console.log(this.state.test);


    return(
      <div>
        <div className="container">
          <div className="row">
          <div className="col border">
            <h2>Izvlačenje</h2>
          </div>
          <div className="col border">
            <h2>Brojevi</h2>
          <div>
          {listItems}
          </div>
          <h3>Odabrani brojevi su</h3>
          <p>{this.state.ticket}</p>
          </div>
        </div>
        <div className="row">
          <div className="col">
          {listTickets}
          </div>
          <div className="col">
          <button type="button" className="btn btn-primary" id={this.colorButton ? 'no' : ''} onClick={this.addTicket}  >Dodaj tiket</button>
          <button type="button" className="btn btn-primary play" onClick={this.playTicket}>Odigraj</button>
          </div>
        </div>
      </div>
      </div>)
  }
}


export default App;
