import React from 'react';
import './App.css';

const numbers = [];
const min = 1;
const max = 30;
const randomNumbers = [];

for (let i=1; i<31; i++) {
  numbers.push(i)
}

for(let i = 0; i<12; i++){
  randomNumbers[i]=Math.floor(min + Math.random() * (max - min));

 }


console.log(randomNumbers);
class App extends React.Component {
  constructor() {
    super();

    this.state= {
      ticket: [],
      tickets: [],
      numberTicket: 0,
      number: null, 
      random: [],
      isPlaying: false,
      test: [],
      drawing: null
    }

    this.makeTicket=this.makeTicket.bind(this);
    this.addTicket=this.addTicket.bind(this);
  }

  makeTicket(e) {
    e.preventDefault();
    const validateTicket = this.state.numberTicket;
    const ticketInProgres = this.state.ticket;
      if(validateTicket <5 && ticketInProgres.length < 5) {
      const number = e.target.value;
        this.setState({
        number: number,
        ticket: [...this.state.ticket, number]
        })
      } 

  }

  addTicket(e) {
    e.preventDefault();
    const ticket =this.state.ticket;
    const numberOfTickets = this.state.numberTicket;
    if (numberOfTickets<5 && ticket.length>0) {
      this.setState({
            ...this.state,
            tickets: [...this.state.tickets, ticket ],
            ticket: [],
            numberTicket: this.state.numberTicket +1
        })
    }

  }


  render() {
    let id= this.state.number;
    let element = document.getElementById(id);
    if(id) {
      element.style.backgroundColor='red'
    }

    const listItems=numbers.map((number, index) => 
       <button key={index} value={number} className="number" id={number} onClick={this.makeTicket}>{number}</button>
        );
    const tickets=this.state.tickets;
    const listTickets= tickets.map((ticket, index) => <p key={index}>{ticket}</p>);

    console.log(this.state.numberTicket);
    console.log(this.state.test);    

    return(
      <div>
        <div className="container">
          <div className="row">
          <div className="col border">
            <h2>Izvlaƒçenje</h2>
            <Drawing drawing={this.state.drawing} />
          </div>
          <div className="col border">
            <h2>Brojevi</h2>
            <div>
            {listItems}
            </div>

          </div>
        </div>
        <div className="row">
          <div className="col">
          {listTickets}
          </div>
          <div className="col">
          <button type="button" className="btn btn-primary" id={this.state.numberTicket === 5 ? 'no' : ''} onClick={this.addTicket}  >Dodaj tiket</button>
          <button type="button" className="btn btn-primary play" id={this.state.numberTicket === 5 ? 'yes' : ''} onClick={() => this.setState({drawing:'start'})}>Odigraj</button>
          <h3>Odabrani brojevi su</h3>
          <p>{this.state.ticket}</p>
          </div>
        </div>
      </div>
      </div>)
  }
}

class Drawing extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      number: 1,
      randomNiz: []};

  }

  componentDidMount() {
    this.interval = setInterval(() => {
      console.log(this.state.number, this.props.drawing)
      if (this.props.drawing==='start' && this.state.randomNiz.length < 12) {
        this.setState({randomNiz: [...this.state.randomNiz, Math.floor(1 + Math.random() * (30 - 1))]});        
      } else {
        clearInterval(this.interval);
      }
    }, 2000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    return (
      <div>
        <h1>{this.state.randomNiz}</h1>

      </div>
    )
  }
}

export default App;
