import React from 'react';
import './App.css';

const numbers = [];


for (let i=1; i<31; i++) {
  numbers.push(i)
}

class App extends React.Component {
  render() {
    return(
      <div className="app container">
        <div className="row">
          <div className="col border">
            <Drawing />
          </div>
          <div className="col border">
            <Numbers />
          </div>
        </div>
        <div className="row">
          <div className="col">
          </div>
          <div className="col">

          </div>

        </div>
      </div>)
  }
}

class Numbers extends React.Component {
  constructor() {
    super();

    this.state= {
      ticket: [],
      tickets: [],
      ticketFinished: false,
      numberTicket: 0,
      addClass: false
    }

    this.makeTicket=this.makeTicket.bind(this);
    this.addTicket=this.addTicket.bind(this);
  }

  makeTicket(e) {
    e.preventDefault();
    const ticketInProgres = this.state.ticket;
    this.setState({
      addClass: true
    })
    if(ticketInProgres.length < 5) {
    const number = e.target.value;
      this.setState({
      ticket: [...this.state.ticket, number]
    })
    } else {
      this.setState({
        ticketFinished: true
      })
    }
    console.log(this.state.ticket);
    console.log(this.state.numberTicket);
  }

  addTicket(e) {
    e.preventDefault();
    const validation = this.state.ticketFinished;
    const ticket =this.state.ticket;
    if (validation || ticket) {
      this.setState({
            ...this.state,
            tickets: [...this.state.tickets, ticket ]
        })
    }
    console.log(this.state.tickets);
  }

  render() {
    let numberClass = ["number"];
    if(this.state.addClass) {
      numberClass.push('clicked');
    }

    const listItems=numbers.map((number, index) => 
       <button key={index} value={number} className={numberClass.join(' ')} onClick={this.makeTicket}>{number}</button>
        );
    const tickets=this.state.tickets;
    const listTickets= tickets.map((ticket, index) =>
      <li key={index}>{ticket}</li>);


    return(
      <div>
      <h2>Brojevi</h2>
        <div>
        {listItems}
        </div>
        <button type="button" className="btn btn-primary" onClick={this.addTicket}>Dodaj tiket</button>
        <div>
        {listTickets}
        </div>
      </div>)
  }
}

class Drawing extends React.Component {
  render() {
    return(
      <div className="drawing">
      <h2>Izvlaƒçenje</h2>
      </div>)
  }
}

export default App;
