import React from 'react';
import './App.css';

const numbers = [];

for (let i=1; i<31; i++) {
  numbers.push(i)
}

class App extends React.Component {
  render() {
    return(
      <div className="app container">
        <div className="row">
          <div className="col border">
            <Drawing />
          </div>
          <div className="col border">
            <Numbers />
          </div>
        </div>
        <div className="row">
          <div className="col">
            <Ticket />
          </div>
          <div className="col">
            <Play />
          </div>

        </div>
      </div>)
  }
}

class Numbers extends React.Component {
  constructor() {
    super();

    this.state= {
      ticket: []
    }
  }

  makeTicket(e) {
    const number = e.target.value;
    this.setState({
      ticket: [number]
    })
    console.log(this.ticket);
  }
  render() {
    const listItems=numbers.map((number, index) => 
        <button key={index} value={number} className="number" onClick={this.makeTicket}>{number}</button>
        )
    return(
      <div>
      <h2>Brojevi</h2>
        <div>
        {listItems}
        </div>
      </div>)
  }
}

