import React from 'react';
import './App.css';


class App extends React.Component {
  constructor() {
    super();

    this.state= {
      ticket: [],
      bet: '',
      betArray: [],
      tickets: [],
      numberTicket: 0,
      number: null, 
      random: [],
      isPlaying: false,
      randomArray: [],
      test: [],
      constraint: false,
      ticketsLength: [],
      gain: [],
      winingText: []
    }

    this.makeTicket=this.makeTicket.bind(this);
    this.addTicket=this.addTicket.bind(this);
    this.drawing=this.drawing.bind(this);
    this.compare=this.compare.bind(this);
    this.buttonColoring=this.buttonColoring.bind(this);
    this.addBet=this.addBet.bind(this);
    this.winner=this.winner.bind(this);
    this.quotes=this.quotes.bind(this);
    this.showWinningTicket= this.showWinningTicket.bind(this);
  }

  makeTicket(e) {
    e.preventDefault();
    const validateTicket = this.state.numberTicket;
    const ticketInProgres = this.state.ticket;
    const number = e.target.value;
      if(validateTicket <5 && ticketInProgres.length < 5 && !ticketInProgres.includes(number)) {
        this.setState({
        number: number,
        ticket: [...this.state.ticket, number]
        })
      } 

  }

  addBet(e) {
    e.preventDefault();
    const newBet = e.target.value;
    if (/^\d+$/.test(newBet) && newBet<100000000) {
      this.setState({
        constraint: false,
        bet: newBet
      })
    } else {
      this.setState({
        constraint: true
      })
    }


  }

  addTicket(e) {
    e.preventDefault();
    const ticket =this.state.ticket;
    const numberOfTickets = this.state.numberTicket;
    if (numberOfTickets<5 && ticket.length>0 && this.state.bet>0) {
      this.setState({
            ...this.state,
            tickets: [...this.state.tickets, ticket ],
            betArray: [...this.state.betArray, this.state.bet],
            ticket: [],
            bet: '',
            numberTicket: this.state.numberTicket +1,
            ticketsLength: [...this.state.ticketsLength, ticket.length]
        })
    }
  }

  drawing() {
    this.interval = setInterval(() => {  
      if (this.state.randomArray.length < 12) {
        let newNumber= Math.floor(1 + Math.random() * (30 - 1));
        if(!this.state.randomArray.includes(newNumber))
        this.setState({randomArray: [...this.state.randomArray, newNumber]});        
      } else {
        clearInterval(this.interval);
        this.compare(this.state.randomArray, this.state.tickets);  
      }
    }, 1000);
  }

  compare(random, arr) {

  let myArray = arr;
  let firstArr = random;

  for(let i = 0; i < myArray.length; i++){
      let joinedArray= myArray[i].join(',');
      let newArray = joinedArray.split(',').map(x => parseInt(x, 10))
      let presek = firstArr.filter(x => newArray.includes(x));
      if (presek.length === newArray.length){
        this.setState({
            test: [...this.state.test, true],
          })
      } else {
        this.setState({
            test: [...this.state.test, false],
          })
      }
    console.log(this.state.test);
   } 
   this.winner();

  }

  winner() {
    const testing= this.state.test;
    for(let i=0; i<5; i++){
      let gain= this.quotes(i) * this.state.betArray[i];
      if(testing[i]===true){
        this.setState({
          gain: [...this.state.gain, gain]
        });
        let element= document.getElementById('ticket'+i);
        element.style.backgroundColor= 'red';
      } else {
        this.setState({
          gain: [...this.state.gain, 0]
        });
      }
    }
    for(let i=0; i<5; i++){
    if(this.state.gain[i]>0){
      this.setState({
        winingText: [...this.state.winingText, 'ticket sa brojem ' + (i+1) + ' ima dobitak od ' + this.state.gain[i]]
      })
    }
  }

  }

 showWinningTicket() {
  for(let i=0; i<5; i++){
    if(this.state.gain[i]>0){
      return <p>{'ticket sa brojem ' + (i+1) + ' ima dobitak od ' + this.state.gain[i]}</p>
    }
  }
 }


  buttonColoring(number) {
    if(number===1 || number ===9 || number===17 || number === 25) {
      return 'borderGreen'
    }else if(number===2 || number ===10 || number===18 || number === 26){
      return 'borderBlue'
    }else if(number===3 || number ===11 || number===19 || number === 27){
      return 'borderRed'
    }else if(number===4 || number ===12 || number===20 || number === 28){
      return 'borderViolet'
    }else if(number===5 || number ===13 || number===21 || number === 29){
      return 'borderYellow'
    }else if(number===6 || number ===14 || number===22 || number === 30){
      return 'borderOgrange'
    }else if(number===7 || number ===15 || number===23){
      return 'borderBrown'
    }else {
      return 'borderGray'
    }
  }

  quotes(index) {
    const quote1= 4;
    const quote2= 8;
    const quote3= 12;
    const quote4= 16;
    const quote5= 20;

      if(this.state.ticketsLength[index]===1){
        return quote1
      }else if(this.state.ticketsLength[index]===2){
        return quote2
      }else if(this.state.ticketsLength[index]===3){
        return quote3
      }else if(this.state.ticketsLength[index]===2){
        return quote4
      }else {
        return quote5
      }
  }

  render() {
    let id= this.state.number;
    let element = document.getElementById(id);
    if(id) {
      element.style.backgroundColor='red';
      element.style.borderColor= 'red'
    }

    const numbers = [];

    for (let i=1; i<31; i++) {
      numbers.push(i);
    }

    const listItems=numbers.map((number, index) => 
       <button key={index} value={number} className={'number ' + (this.buttonColoring(number))} id={number} onClick={this.makeTicket}>{number}</button>
        );

    const showBet = this.state.betArray.map((bet, index) => <p key={index} id={'bet'+(index)}>{(bet) + ' ' + (this.quotes(index))}</p>);
    const tickets=this.state.tickets;
    const listTickets= tickets.map((ticket, index) => <div key={index} id={'ticket'+(index)}>{
      ticket.map((number, index)=>
        <button key={index} value={number} className="numbersOnTicket">{number}</button>)}</div>
        );

    const randomArray =this.state.randomArray;
    const showArray=randomArray.map((array, index)=> <button key={index} className="number">{array}</button>);

    const currentNumbers= this.state.ticket;
    const pickedNumbers= currentNumbers.map((num, index) => <button key={index} className="numbersOnTicket">{num}</button>);

    return(
      <div>
        <div className="container">
          <div className="row">
          <div className="col border">
            <h2>Izvlačenje</h2>
            <p>{showArray}</p>
          </div>
          <div className="col border">
            <h2>Brojevi</h2>
            <div>
            {listItems}
            </div>

          </div>
        </div>
        <div className="row">
          <div className="col">
            <div className="tickets">
            {listTickets}
            </div>
            <div className="bets">
            {showBet}
            </div>
          </div>
          <div className="col">
          <button type="button" className="btn btn-primary" id={this.state.numberTicket === 5 ? 'no' : ''} onClick={this.addTicket}  >Dodaj tiket</button>
          <button type="button" className="btn btn-primary play" id={this.state.numberTicket === 5 ? 'yes' : ''} onClick={this.drawing}>Odigraj</button>
          <input 
              placeholder="bet"
              onChange={this.addBet}
              value={this.state.bet}
            />
            <p className={this.state.constraint === true? '' : 'play'}>Možete uneti samo brojeve</p>
          <h3>Odabrani brojevi su</h3>
          <p>{pickedNumbers}</p>
          </div>
        </div>
      </div>
      </div>)
  }
}


export default App;
