import React from 'react';
import './App.css';

const numbers = [];

for (let i=1; i<31; i++) {
  numbers.push(i)
}


class App extends React.Component {
  render() {
    return(
      <div className="app container">
        <div className="row">
          <div className="col">
            <Drawing />
          </div>
          <div className="col">
            <Numbers />
          </div>
        </div>
        <div className="row">
          <Play />
        </div>
      </div>)
  }
}

class Numbers extends React.Component {
  render() {
    const listItems=numbers.map(number => 
          <button>{number}</button>
        )
    return(
      <div>
      <h2>Numbers</h2>
        <div>
        {listItems}
        </div>
      </div>)
  }
}

class Play extends React.Component {
  render() {
    return(
      <div>
      </div>)
  }
}

class Drawing extends React.Component {
  render() {
    return(
      <div className="drawing">
      <h2>Drawing</h2>
      </div>)
  }
}
export default App;
