import React from 'react';
import './App.css';

const numbers = [];


for (let i=1; i<31; i++) {
  numbers.push(i)
}

class App extends React.Component {
  render() {
    return(
      <div className="app container">
        <div className="row">
          <div className="col border">
            <Drawing />
          </div>
          <div className="col border">
            <Numbers />
          </div>
        </div>
        <div className="row">
          <div className="col">
            <Ticket />
          </div>
          <div className="col">

          </div>

        </div>
      </div>)
  }
}

class Numbers extends React.Component {
  constructor() {
    super();

    this.state= {
      ticket: [],
      tikets: [],
      ticketFinished: false,
      numberTicket: 0
    }

    this.makeTicket=this.makeTicket.bind(this);
    this.addTicket=this.addTicket.bind(this);
  }

  makeTicket(e) {
    e.preventDefault();
    const ticketInProgres = this.state.ticket;

    if(ticketInProgres.length < 5) {
    const number = e.target.value;
      this.setState({
      ticket: [...this.state.ticket, number]
    })
    } else {
      this.setState({
        ticketFinished: true,
      })
    }
    console.log(this.state.ticket);
    console.log(this.state.numberTicket);
  }

  addTicket(e) {
    e.preventDefault();
    const ticketLottery=this.state.ticket;
    const validation = this.state.ticketFinished;
    if (validation) {
      this.setState({
        tickets: [...this.state.tickets, ticketLottery]
      })
    }
    console.log(this.state.tickets);
  }

  render() {
    const listItems=numbers.map((number, index) => 
        <button key={index} value={number} className="number" onClick={this.makeTicket}>{number}</button>
        )
    return(
      <div>
      <h2>Brojevi</h2>
        <div>
        {listItems}
        </div>
        <button type="button" className="btn btn-primary" onClick={this.addTicket}>Dodaj tiket</button>
        <div>
        </div>
      </div>)
  }
}

class Ticket extends React.Component {
  constructor(props) {
    super(props);

    this.state= {
      ticketOne: [],
      ticketTwo: [],
      ticketThree: [],
      ticketFour: [],
      ticketFive: []
    }
  }
  render() {
    return(
      <div>

      </div>)
  }
}

class Drawing extends React.Component {
  render() {
    return(
      <div className="drawing">
      <h2>Izvlačenje</h2>
      </div>)
  }
}

export default App;
