import React from 'react';
import './App.css';

const numbers = [];
const min = 1;
const max = 30;
const randomNumbers = [];

for (let i=1; i<31; i++) {
  numbers.push(i)
}

for(let i = 0; i<12; i++){
  randomNumbers[i]=Math.floor(min + Math.random() * (max - min));

 }

console.log(randomNumbers);
