      <div className="app container">
        <div className="row">
          <div className="col border">
            <Drawing />
          </div>
          <div className="col border">
            <Numbers />
          </div>
        </div>
        <div className="row">
          <div className="col">
          </div>
          <div className="col">

          </div>

        </div>
      </div>)
  }
}

class Numbers extends React.Component {
  constructor() {
    super();

    this.exampleRef = React.createRef();
    this.state= {
      ticket: [],
      tickets: [],
      ticketFinished: false,
      numberTicket: 0,
      addClass: false,
      number: null, 
      random: []
    }

    this.makeTicket=this.makeTicket.bind(this);
    this.addTicket=this.addTicket.bind(this);
    this.playTicket=this.playTicket.bind(this);
  }

  makeTicket(e) {
    e.preventDefault();
    const validateTicket = this.state.numberTicket;
    const ticketInProgres = this.state.ticket;
    if(validateTicket<5){
      if(ticketInProgres.length < 5) {
      const number = e.target.value;
        this.setState({
        addClass: true,
        number: number,
        ticket: [...this.state.ticket, number]
      })
      } else {
        this.setState({
          ticketFinished: true
        })
      }
    }
  }

  addTicket(e) {
    e.preventDefault();
    const validation = this.state.ticketFinished;
    const ticket =this.state.ticket;
    const numberOfTickets = this.state.numberTicket;
    if(numberOfTickets < 5){
      if (validation || ticket.length>0) {
        this.setState({
              ...this.state,
              tickets: [...this.state.tickets, ticket ],
              ticket: [],
              ticketFinished: false,
              numberTicket: this.state.numberTicket +1
          })
      }
    }else{
        const addButton = document.getElementById('add');
        const playButton = document.getElementById('play');
        addButton.style.display='none';
        playButton.style.display='block'; 
    }
  }

  playTicket() {
    <Drawing clicked="true"/>
  }

  render() {
    let id= this.state.number;
    let element = document.getElementById(id);
    if(id) {
      element.style.backgroundColor='red'
    }

    const listItems=numbers.map((number, index) => 
       <button key={index} value={number} className="number" id={number} onClick={this.makeTicket}>{number}</button>
        );
    const tickets=this.state.tickets;
    const listTickets= tickets.map((ticket, index) =>
      <li key={index}>{ticket}</li>);

    return(
      <div>
      <h2>Brojevi</h2>
        <div>
        {listItems}
        </div>
        <h3>Odabrani brojevi su</h3>
        <p>{this.state.ticket}</p>
        <button type="button" className="btn btn-primary" id="add" onClick={this.addTicket}>Dodaj tiket</button>
        <button type="button" className="btn btn-primary play" id="play" onClick={this.playTicket}>Odigraj</button>

        <div>
        {listTickets}
        </div>
      </div>)
  }
}

